mapper.select:
SELECT A.DOC_NO, A.TITLE, A.CONTENT, A.WRITER, A.REG_DTTM, A.VIEW, A.REPLY
  FROM (
        SELECT A.*, (@ROWNUM := @ROWNUM + 1) AS ROWNUM
          FROM (
                SELECT A.DOC_NO, A.TITLE, A.CONTENT, A.WRITER, A.REG_DTTM, A.VIEW, (SELECT COUNT(*) FROM TB_REPLY X WHERE X.DOC_NO = A.DOC_NO) AS REPLY
                  FROM TB_BOARD A




                        AND A.DOC_NO = #{schVal}


                        AND A.TITLE LIKE CONCAT('%', #{schVal}, '%')


                        AND A.WRITER LIKE CONCAT('%', #{schVal}, '%')




                ORDER BY

                  ${sort}

                ) A, (SELECT @ROWNUM := 0) TEMP
        ) A
 WHERE ROWNUM BETWEEN #{startNo} AND #{endNo}

SELECT A.DOC_NO, A.TITLE, A.CONTENT, A.WRITER, A.REG_DTTM, A.VIEW, (SELECT COUNT(*) FROM TB_REPLY X WHERE X.DOC_NO = A.DOC_NO) AS REPLY
  FROM TB_BOARD A
 WHERE A.DOC_NO = #{docNo}

mapper.update:
UPDATE TB_BOARD
   SET TITLE = #{title}
     , CONTENT = #{content}
 WHERE DOC_NO = #{docNo}

UPDATE TB_BOARD
   SET VIEW = VIEW + 1
 WHERE DOC_NO = #{docNo}

mapper.select:
SELECT REPLY_NO, DOC_NO, WRITER, CONTENT, REG_DTTM
  FROM TB_REPLY
 WHERE DOC_NO = #{docNo}

mapper.insert:

  SELECT IFNULL(MAX(REPLY_NO), 0) + 1 AS REPLY_NO FROM TB_REPLY WHERE DOC_NO = #{docNo}

INSERT INTO TB_REPLY (REPLY_NO, DOC_NO, WRITER, CONTENT)
VALUES (#{replyNo}, #{docNo}, #{writer}, #{content})

mapper.update:
UPDATE TB_REPLY
   SET CONTENT = #{content}
 WHERE REPLY_NO = #{replyNo}
   AND DOC_NO = #{docNo}

mapper.delete:
DELETE FROM TB_REPLY
 WHERE DOC_NO = #{docNo}

   AND REPLY_NO = #{replyNo}
